import {
   INCREMENT_CELL,
   CREATE_MATRIX,
   REMOVE_ROW,
   ADD_ROW,
   SET_NEAREST_IDS,
   RESET_NEAREST_IDS,
} from "../types";

const initialState = { nearestIds: [], data: [] };
//TODO: row 1 column 3 cells 2
const findNearestIds = (matrixData, cellsCount, id) => {
   if (+cellsCount === 0) {
      return [];
   } else {
      let nearestIds = [];
      let sortedMatrix = matrixData
         .reduce((currentRow, nextRow) => currentRow.concat(nextRow))
         .sort((a, b) => a.value - b.value);
      const iterationCount =
         cellsCount < sortedMatrix.length
            ? cellsCount
            : sortedMatrix.length - 1;
      let indexCurrentId = sortedMatrix.indexOf(
         sortedMatrix.find((item) => item.id === id)
      );
      //exclusion when set count of cells more than the available count of cells
      const exclusionIds = (indexCurrentId) =>
         sortedMatrix
            .filter((item, index) => index !== indexCurrentId)
            .forEach((item) => nearestIds.push(item.id));
      //get ids when the hovered item is the lowest
      if (indexCurrentId === 0) {
         sortedMatrix.length - 1 === iterationCount
            ? exclusionIds(indexCurrentId)
            : sortedMatrix
                 .splice(1, iterationCount)
                 .forEach((item) => nearestIds.push(item.id));
      }
      //get ids when the hovered item is the biggest
      else if (indexCurrentId === sortedMatrix.length - 1) {
         sortedMatrix.length - 1 === iterationCount
            ? exclusionIds(indexCurrentId)
            : sortedMatrix
                 .splice(-iterationCount - 1, iterationCount)
                 .forEach((item) => nearestIds.push(item.id));
      }
      //other options
      else {
         if (sortedMatrix.length - 1 === iterationCount) {
            exclusionIds(indexCurrentId);
         } else {
            // const currentValue = sortedMatrix.find((item) => item.id === id)
            //    .value;
            for (let i = 0; i < iterationCount; i++) {
               // const sortedValues = sortedMatrix
               //    .map((data) => data.value)
               //    .sort((a, b) => a - b);
               // console.log(sortedMatrix);
               // const indexCurrentValue = sortedValues.indexOf(currentValue);
               console.log(indexCurrentId, sortedMatrix);
               /*   if (indexCurrentId === 0) {
                  sortedMatrix
                     .splice(1, iterationCount - i)
                     .forEach((item) => nearestIds.push(item.id));
                  break;
               } else if (indexCurrentId === sortedMatrix.length - 1) {
                  sortedMatrix
                     .splice(-iterationCount - i - 1, iterationCount - i)
                     .forEach((item) => nearestIds.push(item.id));
                  break;
               } else  */
               {
                  const prevValue = sortedMatrix[indexCurrentId - 1].value;
                  console.log(prevValue);
                   const nextValue = sortedMatrix.find(
                     (item) => item[indexCurrentId + 1]
                  ).value;
                  const currentValue = sortedMatrix.find(
                     (item) => item[indexCurrentId]
                  ).value;
                  console.log(prevValue, nextValue, currentValue);
                  const prevDiff = Math.abs(currentValue - prevValue);
                  const nextDiff = Math.abs(nextValue - currentValue);
                  const nearestValue =
                     prevDiff < nextDiff ? prevValue : nextValue;
                  const nearestId = sortedMatrix.find(
                     (item) => item.value === nearestValue
                  ).id;
                  nearestIds.push(nearestId);
                  sortedMatrix.splice(
                     sortedMatrix.indexOf(
                        sortedMatrix.find((item) => item.id === nearestId)
                     ),
                     1
                  );
               }
            }
         }
      }
      return nearestIds;
   }
};

const matrixReducer = (state = initialState, action) => {
   let settings, data, ids;
   switch (action.type) {
      case CREATE_MATRIX:
         data = [...state.data];
         settings = action.settings;
         for (let i = 0; i < settings.rows; i++) {
            data.push([]);
         }
         data.forEach((item, index) => {
            for (let i = 0; i < settings.columns; i++) {
               const cellObject = {
                  id: String(index + 1).concat(i + 1),
                  value: Math.round(100 + Math.random() * 899),
               };
               item.push(cellObject);
            }
         });
         return { ...state, data };
      case INCREMENT_CELL:
         data = [...state.data];
         data
            .find((row) => row.find((item) => item.id === action.id))
            .filter((item) => item.id === action.id)[0].value++;
         return { ...state, data };
      case REMOVE_ROW:
         data = [...state.data];
         data.splice(action.index, 1);
         return { ...state, data };
      case ADD_ROW:
         data = [...state.data];
         let newRow = [];
         for (let i = 0; i < action.columnsCount; i++) {
            const cellObject = {
               id: String(data.length + 1).concat(i + 1),
               value: Math.round(100 + Math.random() * 899),
            };
            newRow.push(cellObject);
         }
         data.push(newRow);
         return { ...state, data };
      case SET_NEAREST_IDS:
         data = [...state.data];
         const cellsCount = action.cellsCount;
         const id = action.id;
         ids = findNearestIds(data, cellsCount, id);
         return { ...state, nearestIds: ids };
      case RESET_NEAREST_IDS:
         ids = [...state.nearestIds];
         ids = [];
         return { ...state, nearestIds: ids };
      default:
         return state;
   }
};

export default matrixReducer;
