import React, { useEffect } from "react";
import TableCell from "./TableCell/TabeCell";
import NumerableCell from "./TableCell/NumerableCell";

const TableRow = ({ data }) => {
   let matrixCells = data.map((cellData, index) => {
      if (cellData.rowNumber) {
         return <NumerableCell key={index} value={cellData.rowNumber} />;
      }
      return <TableCell key={index} value={cellData.value} />;
   });

   const generalCells = useMemo(() => {
      const sumRow = data
         .filter((cellData) => cellData.value)
         .map((data) => data.value)
         .reduce((sum = 0, value) => sum + value);
      return matrixCells.push(<NumerableCell key={sumRow} value={sumRow} />);
   }, [matrixCells]);
   useEffect(() => {}, [data]);

   return <tr>{generalCells}</tr>;
};

export default TableRow;
