import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { cellRenderers } from "../../../../../CellRenderers";
import { removeRow, setNearestValues } from "../../../../../redux/actions";
import CrossButton from "../../../../CrossButton";
import TableCell from "./TableCell";

const findNearestValues = (cellsCount, currentValue) => {
   if (+cellsCount === 0) {
      return;
   } else {
      let sortedValues = matrixData
         .reduce((currentRow, nextRow) => currentRow.concat(nextRow))
         .map((data) => data.value)
         .sort((a, b) => a - b);
      let nearestValues = [];
      const iterationCount =
         cellsCount < sortedValues.length
            ? cellsCount
            : sortedValues.length - 1;
      for (let i = 0; i < iterationCount; i++) {
         const indexCurrentValue = sortedValues.indexOf(currentValue);
         if (indexCurrentValue === 0) {
            nearestValues.push(sortedValues[indexCurrentValue + 1]);
            sortedValues.splice(indexCurrentValue + 1, 1);
         } else if (indexCurrentValue === sortedValues.length - 1) {
            nearestValues.push(sortedValues[indexCurrentValue - 1]);
            sortedValues.splice(indexCurrentValue - 1, 1);
         } else {
            const prevValue = sortedValues[indexCurrentValue - 1];
            const prevDiff = Math.abs(currentValue - prevValue);
            const nextValue = sortedValues[indexCurrentValue + 1];
            const nextDiff = Math.abs(nextValue - currentValue);
            const nearest = prevDiff < nextDiff ? prevValue : nextValue;
            nearestValues.push(nearest);
            sortedValues.splice(sortedValues.indexOf(nearest), 1);
         }
      }
      return dispatch(setNearestValues(nearestValues));
   }
};

const TableRow = ({ rowCount, data, cellsSetting }) => {
   const matrixData = useSelector((state) => state.matrix.data);
   const nearestValues = useSelector((state) => state.matrix.nearestValues);
   const dispatch = useDispatch();
   const [sumIsHover, setSumIsHover] = useState(false);

   const rowLabel = (
      <TableCell
         key={rowCount}
         value={rowCount}
         Renderer={cellRenderers.static}
      />
   );
   const rowCells = () => {
      const renderer = sumIsHover
         ? cellRenderers.percentage
         : cellRenderers.increment;

      return data.map((cellData, index) => {
         const percentOfSum = Math.round((cellData.value * 100) / sumRow);
         return (
            <TableCell
               key={cellData.id}
               id={cellData.id}
               value={cellData.value}
               percent={percentOfSum}
               cellHoverHandler={() =>
                  findNearestValues(cellsSetting, cellData.value)
               }
               Renderer={renderer}
            />
         );
      });
   };
   const sumRow = data
      .map((data) => data.value)
      .reduce((sum = 0, value) => sum + value);
   const sumRowCell = (
      <TableCell
         key={sumRow}
         value={sumRow}
         Renderer={cellRenderers.sum}
         sumMouseEnterHandler={() => setSumIsHover(true)}
         sumMouseLeaveHandler={() => setSumIsHover(false)}
      />
   );
   const cells = [rowLabel, ...rowCells(), sumRowCell];

   return (
      <tr>
         {cells}
         <td>
            <CrossButton
               buttonClickHandler={() => dispatch(removeRow(rowCount))}
            />
         </td>
      </tr>
   );
};

export default TableRow;
