import {
   INCREMENT_CELL,
   CREATE_MATRIX,
   REMOVE_ROW,
   ADD_ROW,
   SET_NEAREST_IDS,
   RESET_NEAREST_IDS,
   SET_SETTINGS,
} from "./types";

export const setSettings = (settings) => {
   const rowsCount = settings.rowsCount;
   const columnsCount = settings.columnsCount;
   const cellsCount = settings.cellsCount;
   return {
      type: SET_SETTINGS,
      rowsCount,
      columnsCount,
      cellsCount,
   };
};

export const createMatrix = () => {
   return {
      type: CREATE_MATRIX,
   };
};

export const incrementCell = (id, value) => {
   return {
      type: INCREMENT_CELL,
      id,
      value,
   };
};

export const removeRow = (rowCount) => {
   return {
      type: REMOVE_ROW,
      index: rowCount - 1,
   };
};

export const addRow = (columnsCount) => {
   return {
      type: ADD_ROW,
      columnsCount,
   };
};

export const setNearestIds = (cellsCount, id) => {
   return {
      type: SET_NEAREST_IDS,
      cellsCount,
      id,
   };
};

export const resetNearestIds = () => {
   return {
      type: RESET_NEAREST_IDS,
   };
};
