import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import "./App.css";
import StartPage from "./components/StartPage";
import Main from "./components/Main";
import { setSettings } from "./redux/actions";

function App() {
   const matrixData = useSelector((state) => state.matrix.data);
   const dispatch = useDispatch();

   useEffect(
      matrixData.length === 0
         ? dispatch(
              setSettings({ rowsCount: 0, columnsCount: 0, cellsCount: 0 })
           )
         : null,
      [matrixData]
   );

   return (
      <div className="App">
         {matrixData.length === 0 ? <StartPage /> : <Main />}
      </div>
   );
}

export default App;
