import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
   incrementCell,
   setNearestValues,
} from "../../../../../../../redux/actions";

const IncrementCell = ({ id, value, cellsSetting }) => {
   const nearestIds = useSelector((state) => state.matrix.nearestIds);
   const dispatch = useDispatch();
   let style = null;
   const isNearest = nearestIds.find((nearestId) => nearestId === id)
      ? (style = { backgroundColor: "aqua" })
      : (style = null);

   return (
      <div
         style={style}
         onMouseEnter={() => dispatch(setNearestValues(cellsSetting, value))}
         onMouseLeave={() => {
            console.log(null);
            setStyle(null);
         }}
         onClick={() => dispatch(incrementCell(id, value))}
      >
         {value}
      </div>
   );
};

export default IncrementCell;
