const calculateAverageValues = (matrixData) => {
   const rowsValues = matrixData.map((item) => item.map((row) => row.value));
   let averageValues = [];
   for (let i = 0; i < rowsValues.length; i++) {
      let columnSum = 0;
      for (let j = 0; j < rowsValues[i].length; j++) {
         columnSum += rowsValues[i][j] + rowsValues[i + 1][j];
      }
      const average = columnSum / matrixData.length;
      averageValues.push(average);
   }
   return averageValues;
};
export default calculateAverageValues;

