import { SET_MATRIX_ROWS } from "../types";

const initialState = { settings: { rows: 0, columns: 0, cells: 0 }, data: [] };

const matrixReducer = (state = initialState, action) => {
   switch (action.type) {
      case SET_MATRIX_ROWS:
         const prevSettings = { ...state.settings };
         const newSettings = (prevSettings.rows = action.payload);
         console.log(newSettings)
         return { ...state };
      default:
         return state;
   }
};

export default matrixReducer;
