import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { addRow } from "../../redux/actions";
import Button from "../Button";
import Table from "./Table";

const Main = () => {
   const columnsCount = useSelector(
      (state) => state.matrix.settings.columnsCount
   );
   const matrixData = useSelector((state) => state.matrix.data);

   const dispatch = useDispatch();

   return (
      <main>
         <Button
            buttonText="Add new row"
            buttonClickHandler={() => dispatch(addRow(columnsCount))}
         />
         {matrixData.length === 0 ? <p>Please, set the start settings for the table</p> : <Table />}
      </main>
   );
};

export default Main;
