const matrixReducer = (state = initialState, action) => {
   let settings,
      data,
      nearestIds,
      cellsCount = state.settings.cellsCount;
   switch (action.type) {
      case SET_SETTINGS:
         data = [...state.data];
         settings = { ...action.settings };
         if (
            state.data.length !== 0 &&
            state.settings.columnsCount < +settings.columnsCount
         ) {
            for (
               let i = 0;
               i < settings.columnsCount - state.settings.columnsCount;
               i++
            ) {
               data.forEach((item, index) => {
                  const cellObject = {
                     id: String(index + 1).concat(item.length + 1),
                     value: Math.round(100 + Math.random() * 899),
                  };
                  item.push(cellObject);
               });
            }
         } else if (
            state.data.length !== 0 &&
            state.settings.columnsCount > +settings.columnsCount
         ) {
            for (
               let i = 0;
               i < state.settings.columnsCount - settings.columnsCount;
               i++
            ) {
               data.forEach((item) => {
                  item.pop();
               });
               console.log(data);
            }
         }
         return { ...state, data, settings };
      case CREATE_MATRIX:
         data = [...state.data];
         settings = { ...state.settings };
         for (let i = 0; i < settings.rowsCount; i++) {
            const cells = [];
            for (let i = 0; i < settings.columnsCount; i++) {
               const cellObject = {
                  id: String(data.length + 1).concat(i + 1),
                  value: Math.round(100 + Math.random() * 899),
               };
               cells.push(cellObject);
            }
            data.push(cells);
         }
         return { ...state, data };
      case INCREMENT_CELL:
         data = [...state.data];
         data
            .find((row) => row.find((item) => item.id === action.id))
            .filter((item) => item.id === action.id)[0].value++;
         nearestIds = findNearestIds(data, cellsCount, action.id);
         return { ...state, data, nearestIds };
      case REMOVE_ROW:
         data = [...state.data];
         settings = { ...state.settings };
         data.splice(action.index, 1);
         settings.rowsCount = data.length;
         return { ...state, data, settings };
      case ADD_ROW:
         data = [...state.data];
         settings = { ...state.settings };
         let newRow = [];
         for (let i = 0; i < settings.columnsCount; i++) {
            const cellObject = {
               id: String(data.length + 1).concat(i + 1),
               value: Math.round(100 + Math.random() * 899),
            };
            newRow.push(cellObject);
         }
         data.push(newRow);
         settings.rowsCount = data.length;
         return { ...state, settings, data };
      case SET_NEAREST_IDS:
         data = [...state.data];
         nearestIds = findNearestIds(data, cellsCount, action.id);
         return { ...state, nearestIds };
      case RESET_NEAREST_IDS:
         data = [...state.data];
         nearestIds = [];
         return { ...state, nearestIds };
      default:
         return state;
   }
};

export default matrixReducer;
