import React, { useMemo } from "react";
import { useSelector } from "react-redux";
import AverageRow from "./AverageRow";
import TableRow from "./TableRow";

const TableBody = () => {
   const matrix = useSelector((state) => state.matrix.data);
   let matrixRows = useMemo(
      () => matrix.map((row, index) => <TableRow data={row} key={index} />),
      [matrix]
   );
   const generalRows = useMemo(() => {
      matrixRows.push(<AverageRow />);
      return matrixRows;
   }, [matrixRows]);
   return <tbody>{generalRows}</tbody>;
};

export default TableBody;
