import React, { useState } from "react";
import "./StartPage.scss";

const StartPage = () => {
   const [rows, setRows] = useState(0);
   const [columns, setColumns] = useState(0);
   const [cells, setCells] = useState(0);

   const handleChange = (event) =>{
      setRows(event.target.value)
   }

   // const apllyMatrixSettings = (m, n, x) => {};
   console.log(rows)
   return (
      <div className="start-page">
         <h1>Welcome to Matrix Builder</h1>
         <label htmlFor="rows-count">Set the rows count</label>
         <input
            type="number"
            id="rows-count"
            onChange={handleChange}
         ></input>
         <label htmlFor="columns-count">Set the columns count</label>
         <input type="number" id="columns-count"></input>
         <label htmlFor="cells-count">
            Set the count of cells to show the nearest value
         </label>
         <input type="number" id="cells-count"></input>
         {/* <button onClick=></button> */}
      </div>
   );
};

export default StartPage;
