import {
   INCREMENT_CELL,
   REMOVE_ROW,
   ADD_ROW,
   SET_NEAREST_IDS,
   RESET_NEAREST_IDS,
   SET_SETTINGS,
} from "../types";

const initialState = {
   settings: { rowsCount: 0, columnsCount: 0, cellsCount: 0 },
   nearestIds: { sameValueIds: [], ids: [] },
   data: [],
   sortedData: [],
};
const sortData = (data) => {
   if (data.length > 0) {
      return data.flat().sort((a, b) => a.value - b.value);
   }
};
const addCells = (rowNumber, columnsCount, row = [], step = 0) => {
   while (step < columnsCount) {
      row.push({
         id: String(rowNumber + 1).concat(row.length + 1),
         value: Math.round(100 + Math.random() * 899),
      });
      step++;
   }
   return row;
};
const addRows = (data, rowsCount, columnsCount, step = 0) => {
   while (step < rowsCount) {
      const rowCells = addCells(data.length, columnsCount);
      data.push(rowCells);
      step++;
   }
   return data;
};
const createMatrix = (rowsCount, columnsCount, data = []) => {
   return addRows(data, rowsCount, columnsCount);
};
const rebuildMatrix = (data, prevSettings, newSettings) => {
   const rebuildColumns = () => {
      let columnsCountDiff = 0;
      if (prevSettings.columnsCount < newSettings.columnsCount) {
         columnsCountDiff =
            newSettings.columnsCount - prevSettings.columnsCount;
         data.forEach((row, index) => addCells(index, columnsCountDiff, row));
      } else if (prevSettings.columnsCount > newSettings.columnsCount) {
         columnsCountDiff =
            prevSettings.columnsCount - newSettings.columnsCount;
         data.forEach((row) => row.splice(-columnsCountDiff));
      }
      return data;
   };
   const rebuildRows = () => {
      let rowsCountDiff = 0;
      if (prevSettings.rowsCount < newSettings.rowsCount) {
         rowsCountDiff = newSettings.rowsCount - prevSettings.rowsCount;
         addRows(data, rowsCountDiff, newSettings.columnsCount);
      } else if (prevSettings.rowsCount > newSettings.rowsCount) {
         rowsCountDiff = prevSettings.rowsCount - newSettings.rowsCount;
         data.splice(-rowsCountDiff);
      }
      return data;
   };
   rebuildColumns(data, prevSettings, newSettings);
   rebuildRows(data, prevSettings, newSettings);
   return data;
};
const findNearestIds = (data, cellsCount, targetId) => {
   const checkTargetValueIsLowest = (data, targetId) => {
      if (data.indexOf(data.find((item) => item.id === targetId)) === 0) {
         return true;
      }
   };
   const checkTargetValueIsBiggest = (data, targetId) => {
      if (
         data.indexOf(data.find((item) => item.id === targetId)) ===
         data.length - 1
      ) {
         return true;
      }
   };
   if (cellsCount === 0) {
      return [];
   } else {
      let nearestIds = { sameValueIds: [], ids: [] };
      let sortedData = data;
      const targetValue = sortedData.find((item) => item.id === targetId).value;
      const sameValueIds = sortedData
         .filter((item) => item.value === targetValue && item.id !== targetId)
         .map((item) => item.id);
      if (sameValueIds.length > 0) {
         nearestIds.sameValueIds = sameValueIds;
         sortedData = data.filter((item) => !sameValueIds.includes(item.id));
      }
      if (checkTargetValueIsLowest(sortedData, targetId)) {
         nearestIds.ids = sortedData
            .filter((item) => item.id !== targetId)
            .splice(1, cellsCount)
            .map((item) => item.id);
      } else if (checkTargetValueIsBiggest(sortedData, targetId)) {
         nearestIds.ids = sortedData.splice(-cellsCount);
      }

      return nearestIds;
   }
};
