const matrixReducer = (state = initialState, action) => {
   let settings = { ...state.settings },
      data = [...state.data],
      nearestIds = { ...state.nearestIds },
      sortedData = [...state.sortedData];
   switch (action.type) {
      case SET_SETTINGS:
         const newSettings = { ...action.settings };
         if (data.length === 0) {
            data = createMatrix(
               newSettings.rowsCount,
               newSettings.columnsCount
            );
         } else if (
            settings.rowsCount !== newSettings.rowsCount ||
            settings.columnsCount !== newSettings.columnsCount
         ) {
            rebuildMatrix(data, settings, newSettings);
         }
         sortedData = sortData(data);
         settings = newSettings;
         return { ...state, data, sortedData, settings };
      case ADD_ROW:
         addRows(data, 1, settings.columnsCount);
         settings.rowsCount = data.length;
         sortedData = sortData(data);
         return { ...state, settings, sortedData, data };
      case REMOVE_ROW:
         data.splice(action.rowIndex, 1);
         settings.rowsCount = data.length;
         sortedData = sortData(data);
         return { ...state, data, sortedData, settings };
      case INCREMENT_CELL:
         data.find((row) =>
            row.find((item) => (item.id === action.id ? item.value++ : null))
         );
         sortedData = sortData(data);
         //TODO: here after incrementing targetValue is undefined????
         // nearestIds = findNearestIds(
         //    sortedData,
         //    settings.cellsCount,
         //    action.id
         // );
         return { ...state, data, sortedData, nearestIds };
      case SET_NEAREST_IDS:
         nearestIds = findNearestIds(
            sortedData,
            settings.cellsCount,
            action.id
         );
         return { ...state, nearestIds };
      case RESET_NEAREST_IDS:
         data = [...state.data];
         return { ...state, nearestIds: { sameValueIds: [], ids: [] } };
      default:
         return state;
   }
};

export default matrixReducer;
