import React, { useState } from "react";
import "./StartPage.scss";

const StartPage = () => {
   const [rows, setRows] = useState(0);
   const [columns, setColumns] = useState(0);
   const [cells, setCells] = useState(0);

   const handleChange = (event) =>{
      setRows(event.target.value)
   }

   return (
      <div className="start-page">
         <h1>Welcome to Matrix Builder</h1>
         <label htmlFor="rowsCount">Set the rows count</label>
         <input
            type="number"
            id="rowsCount"
            onChange={()=>handleChange(this.id)}
         ></input>
         <label htmlFor="columnsCount">Set the columns count</label>
         <input type="number" id="columnsCount"></input>
         <label htmlFor="cellsCount">
            Set the count of cells to show the nearest value
         </label>
         <input type="number" id="cellsCount"></input>
         {/* <button onClick=></button> */}
      </div>
   );
};

export default StartPage;
