import React, { useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { removeRow } from "../../../../../redux/actions";
import CrossButton from "../../../../CrossButton";
import IncrementCell from "./TableCell/IncrementCell";
import StaticCell from "./TableCell/StaticCell";
import SumCell from "./TableCell/SumCell";

/* const showPercentage = (ref) => {
   // return (ref.style.background = "red");
}; */
const hoverCellHandler = (data, hoveredValue, cellsSetting) => {
   if (cellsSetting) {
      const concatData = data.reduce((currentData, nextData) =>
         currentData.concat(nextData)
      );
      const sortValues = concatData
         .map((item) => item.value)
         .sort((a, b) => a - b);
      let nearestValues = [];
      let indexOfHoveredValue = sortValues.indexOf(hoveredValue);
      // for (let i = 0; i < cellsSetting; i++) {
      const prevDiff = Math.abs(
         sortValues[indexOfHoveredValue] - sortValues[indexOfHoveredValue - 1]
      );
      // }
   }
};

const TableRow = ({ rowCount, data, cellsSetting }) => {
   const matrix = useSelector((state) => state.matrix);
   // const cellRef = useRef([]);
   const sumRow = data
      .map((data) => data.value)
      .reduce((sum = 0, value) => sum + value);
   const rowLabel = <StaticCell key={rowCount} value={rowCount} />;
   const rowCells = data.map((cellData, index) => {
      // const percentOfSum = Math.round((cellData.value * 100) / sumRow);
      return (
         <IncrementCell
            key={cellData.id}
            id={cellData.id}
            value={cellData.value}
            // percent={percentOfSum}
            // ref={cellRef[index]}
            hoverCellHandler={() =>
               hoverCellHandler(matrix, cellData.value, cellsSetting)
            }
         />
      );
   });
   const sumRowCell = (
      <SumCell
         key={sumRow}
         value={sumRow}
         // hoverHandler={() => showPercentage(cellRef)}
      />
   );
   const cells = [rowLabel, ...rowCells, sumRowCell];
   const dispatch = useDispatch();

   return (
      <tr>
         {cells}
         <td>
            <CrossButton
               buttonClickHandler={() => dispatch(removeRow(rowCount))}
            />
         </td>
      </tr>
   );
};

export default TableRow;
