import {
   INCREMENT_CELL,
   REMOVE_ROW,
   ADD_ROW,
   SET_NEAREST_IDS,
   RESET_NEAREST_IDS,
   SET_SETTINGS,
} from "../types";

const initialState = {
   settings: { rowsCount: 0, columnsCount: 0, cellsCount: 0 },
   nearestIds: [],
   data: [],
};

const addCells = (rowNumber, columnsCount, row = [], step = 0) => {
   while (step < columnsCount) {
      row.push({
         id: String(rowNumber + 1).concat(row.length + 1),
         value: Math.round(100 + Math.random() * 899),
      });
      step++;
   }
   return row;
};
const addRows = (data, rowsCount, columnsCount, step = 0) => {
   while (step < rowsCount) {
      const rowCells = addCells(data.length, columnsCount);
      data.push(rowCells);
      step++;
   }
   return data;
};
const createMatrix = (rowsCount, columnsCount, data = []) => {
   return addRows(data, rowsCount, columnsCount);
};
const rebuildColumns = (data, prevSettings, newSettings) => {
   let columnsCountDiff = 0;
   if (prevSettings.columnsCount < newSettings.columnsCount) {
      columnsCountDiff = newSettings.columnsCount - prevSettings.columnsCount;
      data.forEach((row, index) => addCells(index, columnsCountDiff, row));
   } else if (prevSettings.columnsCount > newSettings.columnsCount) {
      columnsCountDiff = prevSettings.columnsCount - newSettings.columnsCount;
      data.forEach((row) => row.splice(-columnsCountDiff));
   }
   return data;
};
const rebuildRows = (data, prevSettings, newSettings) => {
   let rowsCountDiff = 0;
   if (prevSettings.rowsCount < newSettings.rowsCount) {
   } else if (prevSettings.rowsCount > newSettings.rowsCount) {
      rowsCountDiff = prevSettings.rowsCount - newSettings.rowsCount;
      data.splice(rowsCountDiff);
   }
   return data;
};
const rebuildMatrix = (data, prevSettings, newSettings) => {
   if (prevSettings.columnsCount !== newSettings.columnsCount)
      return rebuildColumns(data, prevSettings, newSettings);
   if (prevSettings.rowsCount !== newSettings.rowsCount)
      return rebuildRows(data, prevSettings, newSettings);
   return data;
};
const findNearestIds = (matrixData, cellsCount, id) => {
   if (+cellsCount === 0) {
      return [];
   } else {
      let nearestIds = [];
      let sortedMatrix = matrixData
         .reduce((currentRow, nextRow) => currentRow.concat(nextRow), [])
         .sort((a, b) => a.value - b.value);
      const iterationCount =
         cellsCount < sortedMatrix.length
            ? cellsCount
            : sortedMatrix.length - 1;
      let indexCurrentId = sortedMatrix.indexOf(
         sortedMatrix.find((item) => item.id === id)
      );
      const exclusionIds = (indexCurrentId) =>
         sortedMatrix
            .filter((item, index) => index !== indexCurrentId)
            .forEach((item) => nearestIds.push(item.id));
      //get ids when the hovered item is the lowest
      if (indexCurrentId === 0) {
         sortedMatrix.length - 1 === iterationCount
            ? exclusionIds(indexCurrentId)
            : sortedMatrix
                 .splice(1, iterationCount)
                 .forEach((item) => nearestIds.push(item.id));
      }
      //get ids when the hovered item is the biggest
      else if (indexCurrentId === sortedMatrix.length - 1) {
         sortedMatrix.length - 1 === iterationCount
            ? exclusionIds(indexCurrentId)
            : sortedMatrix
                 .splice(-iterationCount - 1, iterationCount)
                 .forEach((item) => nearestIds.push(item.id));
      }
      //other options
      else {
         if (sortedMatrix.length - 1 === iterationCount) {
            exclusionIds(indexCurrentId);
         } else {
            for (let i = 0; i < iterationCount; i++) {
               indexCurrentId = sortedMatrix.indexOf(
                  sortedMatrix.find((item) => item.id === id)
               );
               if (indexCurrentId === 0) {
                  sortedMatrix
                     .splice(1, iterationCount - i)
                     .forEach((item) => nearestIds.push(item.id));
                  break;
               } else if (indexCurrentId === sortedMatrix.length - 1) {
                  const from = -indexCurrentId - iterationCount - i - 1;
                  sortedMatrix
                     .splice(from, iterationCount - i)
                     .forEach((item) => nearestIds.push(item.id));
                  break;
               } else {
                  const prevItem = sortedMatrix[indexCurrentId - 1];
                  const nextItem = sortedMatrix[indexCurrentId + 1];
                  const currentItem = sortedMatrix[indexCurrentId];
                  const prevDiff = Math.abs(currentItem.value - prevItem.value);
                  const nextDiff = Math.abs(nextItem.value - currentItem.value);
                  const nearestItem = prevDiff < nextDiff ? prevItem : nextItem;
                  nearestIds.push(nearestItem.id);
                  sortedMatrix.splice(
                     sortedMatrix.indexOf(
                        sortedMatrix.find((item) => item === nearestItem)
                     ),
                     1
                  );
               }
            }
         }
      }
      return nearestIds;
   }
};

const matrixReducer = (state = initialState, action) => {
   let settings = { ...state.settings },
      data = [...state.data],
      nearestIds;
   switch (action.type) {
      case SET_SETTINGS:
         const newSettings = { ...action.settings };
         if (data.length === 0) {
            data = createMatrix(
               newSettings.rowsCount,
               newSettings.columnsCount
            );
         } else if (
            settings.rowsCount !== newSettings.rowsCount ||
            settings.columnsCount !== newSettings.columnsCount
         ) {
            rebuildMatrix(data, settings, newSettings);
         }
         settings = newSettings;
         return { ...state, data, settings };
      case ADD_ROW:
         addRows(data, 1, settings.columnsCount);
         settings.rowsCount = data.length;
         return { ...state, settings, data };
      case REMOVE_ROW:
         data.splice(action.rowIndex, 1);
         settings.rowsCount = data.length;
         return { ...state, data, settings };
      case INCREMENT_CELL:
         data.find((row) =>
            row.find((item) => (item.id === action.id ? item.value++ : null))
         );
         nearestIds = findNearestIds(data, settings.cellsCount, action.id);
         return { ...state, data, nearestIds };
      case SET_NEAREST_IDS:
         nearestIds = findNearestIds(data, settings.cellsCount, action.id);
         return { ...state, nearestIds };
      case RESET_NEAREST_IDS:
         data = [...state.data];
         nearestIds = [];
         return { ...state, nearestIds };
      default:
         return state;
   }
};

export default matrixReducer;
