import React from "react";
import { useSelector } from "react-redux";
import StaticCell from "../CellRenderer/StaticCell";
import RowFragment from "../RowFragment";
import setCellRenderer from "../setCellRenderer";
import calculateAverageValues from "./calculateAverageValues";

const className = "average-cell";

const AverageRow = () => {
   const matrixData = useSelector((state) => state.matrix.data);
   const averageRowValues = calculateAverageValues(matrixData);

   const Renderer = setCellRenderer(showPercentageValue, StaticCell);
   const avgRowCells = averageValues.map((value, index) => (
      <RowFragment
         key={index}
         className={className}
         label="Avg"
         rowValues={averageRowValues}
      />
   ));

   return <tr></tr>;
};

export default AverageRow;
