import {
   INCREMENT_CELL,
   REMOVE_ROW,
   ADD_ROW,
   SET_NEAREST_IDS,
   RESET_NEAREST_IDS,
   SET_SETTINGS,
   RESET_SETTINGS,
} from "../../action_types";
import { createMatrix, rebuildMatrix, addRows } from "./building";
import defaultState from "./defaultState";

const initialState = defaultState;
const sortData = (data) => {
   if (data.length > 0) {
      return data.flat().sort((a, b) => a.value - b.value);
   }
};

//find nearest values functions
const checkTargetValueIsLowest = (sortedData, targetId) => {
   if (
      sortedData.indexOf(sortedData.find((item) => item.id === targetId)) === 0
   ) {
      return true;
   }
};
const checkTargetValueIsBiggest = (sortedData, targetId) => {
   if (
      sortedData.indexOf(sortedData.find((item) => item.id === targetId)) ===
      sortedData.length - 1
   ) {
      return true;
   }
};
const getNearestIdsOfLowestValue = (sortedData, cellsCount) => {
   return sortedData.slice(1, cellsCount + 1).map((item) => item.id);
};
const getNearestIdsOfBiggestValue = (sortedData, cellsCount) => {
   return sortedData.slice(-cellsCount - 1, -1).map((item) => item.id);
};
const getNearestId = (sortedData, targetId) => {
   const indexOfTargetId = sortedData.indexOf(
      sortedData.find((item) => item.id === targetId)
   );
   const prevItem = sortedData[indexOfTargetId - 1];
   const nextItem = sortedData[indexOfTargetId + 1];
   const currentItem = sortedData[indexOfTargetId];
   const prevDiff = Math.abs(currentItem.value - prevItem.value);
   const nextDiff = Math.abs(nextItem.value - currentItem.value);
   const nearestItem = prevDiff < nextDiff ? prevItem : nextItem;
   return nearestItem.id;
};
const findNearestIds = (
   sortedData,
   targetId,
   cellsCount,
   ids = [],
   step = 0
) => {
   if (step < cellsCount) {
      if (checkTargetValueIsLowest(sortedData, targetId)) {
         ids.push(getNearestIdsOfLowestValue(sortedData, cellsCount - step));
         return ids.flat();
      } else if (checkTargetValueIsBiggest(sortedData, targetId)) {
         ids.push(getNearestIdsOfBiggestValue(sortedData, cellsCount - step));
         return ids.flat();
      } else {
         const id = getNearestId(sortedData, targetId);
         ids.push(id);
         sortedData = sortedData.filter((item) => item.id !== id);
         step++;
         return findNearestIds(sortedData, targetId, cellsCount, ids, step);
      }
   }
   return ids;
};
const getIds = (sortedData, cellsCount, targetId) => {
   const targetValue = sortedData.find((item) => item.id === targetId).value;
   const sameValueIds = sortedData
      .filter((item) => item.value === targetValue && item.id !== targetId)
      .map((item) => item.id);
   if (sameValueIds.length > 0) {
      sortedData = sortedData.filter((item) => !sameValueIds.includes(item.id));
   }
   const nearestIds = findNearestIds(sortedData, targetId, cellsCount);
   return { sameValueIds, ids: nearestIds };
};

const matrixReducer = (state = initialState, action) => {
   console.log(state);
   switch (action.type) {
      case SET_SETTINGS:
         let data;
         const newSettings = { ...action.settings };
         if (state.data.length === 0) {
            data = createMatrix(
               newSettings.rowsCount,
               newSettings.columnsCount
            );
         } else if (
            state.settings.rowsCount !== newSettings.rowsCount ||
            state.settings.columnsCount !== newSettings.columnsCount
         ) {
            data = rebuildMatrix(state.data, state.settings, newSettings);
         }
         return {
            ...state,
            data,
            sortedData: sortData(state.data),
            settings: newSettings,
         };
      case RESET_SETTINGS:
         return {
            defaultState,
         };
      case ADD_ROW:
         return {
            ...state,
            settings: { ...state.setings, rowsCount: state.data.length },
            sortedData: sortData(state.data),
            data: addRows(state.data, 1, state.settings.columnsCount),
         };
      case REMOVE_ROW:
         return {
            ...state,
            settings: { ...state.setings, rowsCount: state.data.length },
            data: state.data.splice(action.rowIndex, 1),
            sortedData: sortData(state.data),
         };
      case INCREMENT_CELL:
         return {
            ...state,
            data: data.find((row) =>
               row.find((item) => (item.id === action.id ? item.value++ : null))
            ),
            sortedData: sortData(state.data),
            nearestIds: getIds(
               state.sortedData,
               state.settings.cellsCount,
               action.id
            ),
         };
      case SET_NEAREST_IDS:
         return {
            ...state,
            sortedData: sortData(state.data),
            nearestIds: getIds(
               state.sortedData,
               state.settings.cellsCount,
               action.id
            ),
         };
      case RESET_NEAREST_IDS:
         return { ...state, nearestIds: { sameValueIds: [], ids: [] } };
      default:
         return defaultState;
   }
};

export default matrixReducer;
