import React from "react";
import "./InputCounts.scss";
import Button from "../../../Button";

const InputCounts = ({ id,  value, handleChange, controlHandler }) => {
   const controlClickHandler = (type) => {
      if (type === "increment") {
         controlHandler(++value);
      } else if (type === "decrement") {
         controlHandler(--value);
      }
   };
   return (
      <div className="input-settings">
         <Button
            className="input-control__button"
            buttonText="-"
            buttonClickHandler={() => controlClickHandler("decrement")}
         />
         <input
            className="input-settings__input"
            type="number"
            id={id}
            onChange={handleChange}
            min="0"
            value={value}
         />
         <Button
            className="input-control__button"
            buttonText="+"
            buttonClickHandler={() => controlClickHandler("increment")}
         />
      </div>
   );
};

export default InputCounts;
