import React from "react";
import { useSelector } from "react-redux";
import StaticCell from "../TableRow/CellRenderer/StaticCell";
import SumCell from "../TableRow/CellRenderer/SumCell";

const AverageRow = () => {
   const data = useSelector((state) => state.matrix.data);
   const className = "avarage-cell";
   const rowsValues = data.map((item) => item.map((row) => row.value));
   //current row values and next row values return the sum of values by equal index
   const averageValues = rowsValues
      .reduce((currentRow, nextRow) =>
         currentRow.map((value, index) => value + nextRow[index])
      )
      .map((value) => value / data.length);
   // rowsValues.flat()
   const sumRow = averageValues.reduce((sum, value) => sum + value, 0);
   console.log(sumRow);

   const avgRowLabel = (
      <StaticCell className={className} key="avg" value="Avg" />
   );
   const avgRowCells = averageValues.map((value, index) => (
      <StaticCell className={className} key={index} value={value.toFixed(2)} />
   ));
   const avgRowSum = (
      <SumCell key="avg-sum" className={className} value={sumRow} />
   );
   const cells = [avgRowLabel, ...avgRowCells, avgRowSum];

   return <tr>{cells}</tr>;
};

export default AverageRow;
