import React from "react";
import { useSelector } from "react-redux";
import HeadCell from "./HeadCell/index";

const TableHead = () => {
   //TODO: change the better
   const columns = useSelector((state) => state.matrix.settings.columnsCount);
   const createHeads = () => {
      let heads = [];
      for (let i = 0; i <= columns; i++) {
         if (i === 0) {
            heads.push(<HeadCell key={i} value="â„–" />);
         } else {
            heads.push(<HeadCell key={i} value={"col " + i} />);
         }
      }
      heads.push(<HeadCell key={"sum"} value={"Sum"} />);
      return heads;
   };
   const heads = createHeads();
   return (
      <thead>
         <tr>{heads}</tr>
      </thead>
   );
};

export default TableHead;
