import React from "react";
import { useDispatch } from "react-redux";
import { createMatrix, setSettings } from "../../redux/actions";
import Button from "../Button";
import InputCounts from "./InputCounts";
import "./StartPage.scss";

const StartPage = () => {
   const dispatch = useDispatch();
   const setTableRows = (value) => {
      setSettings({ rowsCount: value });
   };
   const setTableColumns = (value) => {
      setSettings({ columnsCount: value });
   };
   const setTableCells = (value) => {
      setSettings({ cellsCount: value });
   };

   return (
      <div className="start-page">
         <h1>Welcome to Matrix Builder</h1>
         <InputCounts
            id="rows"
            label="Set the rows count"
            handleChange={(event) => setTableRows(event.target.value)}
         />
         <InputCounts
            id="columns"
            label="Set the columns count"
            handleChange={(event) => setTableColumns(event.target.value)}
         />
         <InputCounts
            id="cells"
            label="Set the count of cells to show the nearest value"
            handleChange={(event) => setTableCells(event.target.value)}
         />
         <Button
            buttonText="Apply settings of matrix"
            buttonClickHandler={() => {
               // dispatch(setSettings(s))
               dispatch(createMatrix());
            }}
         />
      </div>
   );
};

export default StartPage;
