import React from "react";
import { useDispatch } from "react-redux";
import { incrementCell } from "../../../../../../../redux/actions";

const IncrementCell = ({
   id,
   value,
   cellMouseEnter,
   isNearest,
   cellMouseLeave,
}) => {
   const dispatch = useDispatch();
   let style = isNearest ? { backgroundColor: "aqua" } : null;

   return (
      <div
         style={style}
         onMouseEnter={cellMouseEnter}
         onMouseLeave={() => cellMouseLeave(style)}
         onClick={() => dispatch(incrementCell(id, value))}
      >
         {value}
      </div>
   );
};

export default IncrementCell;
