import React from "react";
import "./InputCounts.scss";
import InputControl from "./InputControl/";

const InputCounts = ({ id, label, value, handleChange, controlHandler }) => {
   const controlClickHandler = (type) => {
      if (type === "+") {
         console.log(value);
         controlHandler(value++);
         console.log(value);
      } else if (type === "-") {
         controlHandler(value--);
      }
   };
   return (
      <div className="input-wrapper">
         <label htmlFor={id}>{label}</label>
         <InputControl type="-" controlClickHandler={controlClickHandler} />
         <input
            className="input-settings"
            type="number"
            id={id}
            onChange={handleChange}
            min="0"
            value={value}
         />
         <InputControl type="+" controlClickHandler={controlClickHandler} />
      </div>
   );
};

export default InputCounts;
