import React, { useState } from "react";
import { useSelector } from "react-redux";
import StaticCell from "../CellRenderer/StaticCell";
import SumCell from "../CellRenderer/SumCell";
import setCellRenderer from "../setCellRenderer";
import calculateAverageValues from "./calculateAverageValues";

const className = "average-cell";

const AverageRow = () => {
   const matrixData = useSelector((state) => state.matrix.data);
   const averageValues = calculateAverageValues(matrixData);
   const sumRow = averageValues
      .reduce((sum, value) => sum + value, 0)
      .toFixed(2);
   const [showPercentageValue, setShowPercentageValue] = useState(false);

   const Renderer = setCellRenderer(showPercentageValue, StaticCell);
   const avgRowCells = averageValues.map((value, index) => (
      <StaticCell className={className} key={index} value={value.toFixed(2)} />
   ));

   return (
      <tr>
         <StaticCell className={className} key="avg" value="Avg" />
         <SumCell key="avg-sum" className={className} value={sumRow} />
      </tr>
   );
};

export default AverageRow;
