import React, { useMemo } from "react";
import TableCell from "./TableCell/TableCell";
import NumerableCell from "./TableCell/NumerableCell";

const TableRow = ({ data }) => {
   let matrixCells = data.map((cellData, index) => {
      if (cellData.rowNumber) {
         return <NumerableCell key={index} value={cellData.rowNumber} />;
      }
      return <TableCell key={index} id={cellData.id} value={cellData.value} />;
   });

   const sumCell = useMemo(() => {
      return data
         .filter((cellData) => cellData.value)
         .map((data) => data.value)
         .reduce((sum = 0, value) => sum + value);
   }, [matrixCells]);

   return (
      <tr>
         {matrixCells}
         <NumerableCell value={sumCell} />
      </tr>
   );
};

export default TableRow;
