import React, { useState } from "react";
import { useSelector } from "react-redux";
import "./App.css";
import StartPage from "./components/StartPage";
import Main from "./components/Main";

function App() {
   const matrixData = useSelector((state) => state.matrix.data);
   const [tableSettings, setTableSettings] = useState({
      rows: 3,
      columns: 3,
      cells: 3,
   });
   const resetTableSettings = () => {
      setTableSettings({ rows: 0, columns: 0, cells: 0 });
   };
   useEffect(() => {
      if (matrixData.length === 0) {
         resetTableSettings();
      }
   }, [matrixData]);

   return (
      <div className="App">
         {matrixData.length === 0 ? (
            <StartPage
               tableSettings={tableSettings}
               setTableSettings={setTableSettings}
            />
         ) : (
            <Main columns={tableSettings.columns} cells={tableSettings.cells} />
         )}
      </div>
   );
}

export default App;
