import {
   INCREMENT_CELL,
   REMOVE_ROW,
   ADD_ROW,
   SET_NEAREST_IDS,
   RESET_NEAREST_IDS,
   SET_SETTINGS,
} from "../types";

const initialState = {
   settings: { rowsCount: 0, columnsCount: 0, cellsCount: 0 },
   nearestIds: { sameValueIds: [], ids: [] },
   data: [],
   sortedData: [],
};
const sortData = (data) => {
   if (data.length > 0) {
      return data.flat().sort((a, b) => a.value - b.value);
   }
};
//Building matrix functions
const addCells = (rowNumber, columnsCount, row = [], step = 0) => {
   while (step < columnsCount) {
      row.push({
         id: String(rowNumber + 1).concat(row.length + 1),
         value: Math.round(100 + Math.random() * 899),
      });
      step++;
   }
   return row;
};
const addRows = (data, rowsCount, columnsCount, step = 0) => {
   while (step < rowsCount) {
      const rowCells = addCells(data.length, columnsCount);
      data.push(rowCells);
      step++;
   }
   return data;
};
const createMatrix = (rowsCount, columnsCount, data = []) => {
   return addRows(data, rowsCount, columnsCount);
};
const rebuildMatrix = (data, prevSettings, newSettings) => {
   const rebuildColumns = () => {
      let columnsCountDiff = 0;
      if (prevSettings.columnsCount < newSettings.columnsCount) {
         columnsCountDiff =
            newSettings.columnsCount - prevSettings.columnsCount;
         data.forEach((row, index) => addCells(index, columnsCountDiff, row));
      } else if (prevSettings.columnsCount > newSettings.columnsCount) {
         columnsCountDiff =
            prevSettings.columnsCount - newSettings.columnsCount;
         data.forEach((row) => row.splice(-columnsCountDiff));
      }
      return data;
   };
   const rebuildRows = () => {
      let rowsCountDiff = 0;
      if (prevSettings.rowsCount < newSettings.rowsCount) {
         rowsCountDiff = newSettings.rowsCount - prevSettings.rowsCount;
         addRows(data, rowsCountDiff, newSettings.columnsCount);
      } else if (prevSettings.rowsCount > newSettings.rowsCount) {
         rowsCountDiff = prevSettings.rowsCount - newSettings.rowsCount;
         data.splice(-rowsCountDiff);
      }
      return data;
   };
   rebuildColumns(data, prevSettings, newSettings);
   rebuildRows(data, prevSettings, newSettings);
   return data;
};
//
//find nearest values functions
const checkTargetValueIsLowest = (data, targetId) => {
   if (data.indexOf(data.find((item) => item.id === targetId)) === 0) {
      return true;
   }
};
const checkTargetValueIsBiggest = (data, targetId) => {
   if (
      data.indexOf(data.find((item) => item.id === targetId)) ===
      data.length - 1
   ) {
      return true;
   }
};
const getNearestIdsOfLowestValue = (data, cellsCount) => {
   return data.splice(1, cellsCount).map((item) => item.id);
};
const getNearestIdsOfBiggestValue = (data, cellsCount) => {
   return data.splice(-cellsCount - 1, cellsCount).map((item) => item.id);
};
const getNearestId = (data, targetId) => {
   const indexOfTargetId = data.indexOf(
      data.find((item) => item.id === targetId)
   );
   const prevItem = data[indexOfTargetId - 1];
   const nextItem = data[indexOfTargetId + 1];
   const currentItem = data[indexOfTargetId];
   const prevDiff = Math.abs(currentItem.value - prevItem.value);
   const nextDiff = Math.abs(nextItem.value - currentItem.value);
   const nearestItem = prevDiff < nextDiff ? prevItem : nextItem;
   return nearestItem.id;
};
const getIds = (data, targetId, cellsCount, ids = [], step = 0) => {
   while (step < cellsCount) {
      if (checkTargetValueIsLowest(data, targetId)) {
         ids.push(getNearestIdsOfLowestValue(data, cellsCount - step));
         return ids.flat();
      } else if (checkTargetValueIsBiggest(data, targetId)) {
         console.log("isBiggest");
         ids.push(getNearestIdsOfBiggestValue(data, cellsCount - step));
         return ids.flat();
      } else {
         const id = getNearestId(data, targetId);
         ids.push(id);
         data.splice(data.indexOf(data.find((item) => item.id === id)), 1);
         step++;
         return getIds(data, targetId, cellsCount, ids, step);
      }
   }
   return ids;
};

const findNearestIds = (data, cellsCount, targetId) => {
   let sortedData = data;
   const targetValue = sortedData.find((item) => item.id === targetId).value;
   const sameValueIds = sortedData
      .filter((item) => item.value === targetValue && item.id !== targetId)
      .map((item) => item.id);
   if (sameValueIds.length > 0) {
      sortedData = data.filter((item) => !sameValueIds.includes(item.id));
   }
   console.log(
      "hovered",
      targetId,
      targetValue,
      "founded",
      sameValueIds,
      "less",
      sortedData
         .filter((item) => item.value === targetValue && item.id !== targetId)
         .map((item) => item.id),
      sortedData
         .filter((item) => item.value === targetValue && item.id !== targetId)
         .map((item) => item.value).value
   );
   const nearestIds = getIds(sortedData, targetId, cellsCount);

   return { sameValueIds, ids: nearestIds };
};

const matrixReducer = (state = initialState, action) => {
   let settings = { ...state.settings },
      data = [...state.data],
      nearestIds = { ...state.nearestIds },
      sortedData = [...state.sortedData];
   switch (action.type) {
      case SET_SETTINGS:
         const newSettings = { ...action.settings };
         if (data.length === 0) {
            data = createMatrix(
               newSettings.rowsCount,
               newSettings.columnsCount
            );
         } else if (
            settings.rowsCount !== newSettings.rowsCount ||
            settings.columnsCount !== newSettings.columnsCount
         ) {
            rebuildMatrix(data, settings, newSettings);
         }
         console.log(data);
         sortedData = sortData(data);
         settings = newSettings;
         return { ...state, data, sortedData, settings };
      case ADD_ROW:
         addRows(data, 1, settings.columnsCount);
         settings.rowsCount = data.length;
         sortedData = sortData(data);
         return { ...state, settings, sortedData, data };
      case REMOVE_ROW:
         data.splice(action.rowIndex, 1);
         settings.rowsCount = data.length;
         sortedData = sortData(data);
         return { ...state, data, sortedData, settings };
      case INCREMENT_CELL:
         data.find((row) =>
            row.find((item) => (item.id === action.id ? item.value++ : null))
         );
         sortedData = sortData(data);
         //TODO: here after incrementing targetValue is undefined????
         // nearestIds = findNearestIds(
         //    sortedData,
         //    settings.cellsCount,
         //    action.id
         // );
         return { ...state, data, sortedData, nearestIds };
      case SET_NEAREST_IDS:
         nearestIds = findNearestIds(
            sortedData,
            settings.cellsCount,
            action.id
         );
         return { ...state, nearestIds };
      case RESET_NEAREST_IDS:
         data = [...state.data];
         nearestIds = { sameValueIds: [], ids: [] };
         return { ...state, nearestIds: { sameValueIds: [], ids: [] } };
      default:
         return state;
   }
};

export default matrixReducer;
