import React from "react";

const PercentageCell = ({ sumRow, value }) => {
   const percent = ((value * 100) / sumRow).toFixed(2);
   const style = {
      backgroundColor: "#fff",
      background:
         "linear-gradient(to top ,#d18576 0%,rgba(0,0,0,.3) 100%) no-repeat 100% 100%",
   };
   return (
      <td>
         <div
            style={{
               backgroundColor: "#d18576",
               backgroundSize: `100% ${percent}% `,
            }}
         >
            {" "}
            {percent}%
         </div>
      </td>
   );
};

export default PercentageCell;
