import React from "react";
import { useDispatch } from "react-redux";
import { createMatrix, setSettings } from "../../redux/actions";
import Button from "../Button";
import InputCounts from "./InputCounts";
import "./StartPage.scss";

const StartPage = ({ tableSettings, setTableSettings }) => {
   const dispatch = useDispatch();
   const setTableRows = (value) => {
      setSettings({ rowsCount: value });
   };
   const setTableColumns = (value) => {
      const newSettings = { ...tableSettings, columns: value };
      setTableSettings(newSettings);
   };
   const setTableCells = (value) => {
      const newSettings = { ...tableSettings, cells: value };
      setTableSettings(newSettings);
   };

   return (
      <div className="start-page">
         <h1>Welcome to Matrix Builder</h1>
         <InputCounts
            id="rows"
            label="Set the rows count"
            handleChange={(event) => setTableRows(event.target.value)}
            value={tableSettings.rows}
         />
         <InputCounts
            id="columns"
            label="Set the columns count"
            handleChange={(event) => setTableColumns(event.target.value)}
            value={tableSettings.columns}
         />
         <InputCounts
            id="cells"
            label="Set the count of cells to show the nearest value"
            handleChange={(event) => setTableCells(event.target.value)}
            value={tableSettings.cells}
         />
         <Button
            buttonText="Apply settings of matrix"
            buttonClickHandler={() => {
               //TODO: change settibgs that value cells can't be more than cell count
               if (
                  tableSettings.rows > 0 &&
                  tableSettings.columns > 0 &&
                  tableSettings.cells >= 0
               ) {
                  dispatch(createMatrix(tableSettings));
               }
            }}
         />
      </div>
   );
};

export default StartPage;
