import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { createMatrix, rebuildMatrix, setSettings } from "../../redux/actions";
import Button from "../Button";
import InputCounts from "./InputCounts";

const SettingsTable = () => {
   const matrixData = useSelector((state) => state.matrix.data);
   const settings = useSelector((state) => state.matrix.settings);
   const dispatch = useDispatch();

   const setTableRows = (value) => {
      let newSettings = { ...settings };
      newSettings.rowsCount = value;
      return newSettings;
   };
   const setTableColumns = (value) => {
      let newSettings = { ...settings };
      newSettings.columnsCount = value;
      return newSettings;
   };
   const setTableCells = (value) => {
      let newSettings = { ...settings };
      newSettings.cellsCount = value;
      return newSettings;
   };

   return (
      <div className="settings-table">
         <h1>Welcome to Matrix Builder</h1>
         <InputCounts
            id="rows"
            label="Set the rows count"
            value={settings.rowsCount}
            handleChange={(event) =>
               dispatch(setSettings(setTableRows(event.target.value)))
            }
            blurHandle={()=>dispatch(rebuildMatrix())}
         />
         <InputCounts
            id="columns"
            label="Set the columns count"
            value={settings.columnsCount}
            handleChange={(event) =>
               dispatch(setSettings(setTableColumns(event.target.value)))
            }
            blurHandle={()=>dispatch(rebuildMatrix())}
         />
         <InputCounts
            id="cells"
            label="Set the count of cells to show the nearest value"
            value={settings.cellsCount}
            handleChange={(event) =>
               dispatch(setSettings(setTableCells(event.target.value)))
            }
         />
         {matrixData.length === 0 ? (
            <Button
               buttonText="Apply settings of matrix"
               buttonClickHandler={() => dispatch(createMatrix())}
            />
         ) : null}
      </div>
   );
};

export default SettingsTable;
