import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { resetSettings, setSettings } from "../../redux/actions";
import "./SettingsTable.scss";
import Button from "../Button";
import InputCounts from "./InputCounts";
import ErrorHint from "../ErrorHint";

const SettingsTable = () => {
   const matrixData = useSelector((state) => state.matrix.data);
   const settings = useSelector((state) => state.matrix.settings);
   const dispatch = useDispatch();
   const [rowsCount, setRowsCount] = useState(settings.rowsCount);
   const [columnsCount, setColumnsCount] = useState(settings.columnsCount);
   const [cellsCount, setCellsCount] = useState(settings.cellsCount);
   const [isShowErrorHint, setIsShowErrorHint] = useState(false);
   const [errorText, setErrorText] = useState(null);
   const inputClassName = "input-settings";

   useEffect(() => {
      setRowsCount(settings.rowsCount);
      setColumnsCount(settings.columnsCount);
      setCellsCount(settings.cellsCount);
   }, [settings]);
   useEffect(() => {
      if (matrixData.length === 0) {
         dispatch(resetSettings());
      }
   }, [matrixData.length]);

   const applySettingsHandler = () => {
      if (isShowErrorHint) {
         return;
      } else {
         if (rowsCount > 0 && columnsCount > 0) {
            dispatch(setSettings({ rowsCount, columnsCount, cellsCount }));
         }
         if (
            (rowsCount === 0 || rowsCount < 0) &&
            (columnsCount === 0 || columnsCount < 0)
         ) {
            setIsShowErrorHint(true);
            setErrorText("Please, set the correct rows and columns count");
            setTimeout(() => {
               setIsShowErrorHint(false);
            }, 2000);
         } else if (rowsCount === 0 || rowsCount < 0) {
            setIsShowErrorHint(true);
            setErrorText("Please, set the correct rows count");
            setTimeout(() => {
               setIsShowErrorHint(false);
            }, 2000);
         } else if (columnsCount === 0 || columnsCount < 0) {
            setIsShowErrorHint(true);
            setErrorText("Please, set the correct columns count");
            setTimeout(() => {
               setIsShowErrorHint(false);
            }, 2000);
         }
      }
   };

   return (
      <div className="settings-wrapper">
         <InputCounts
            className={inputClassName}
            label="Set the rows count"
            value={rowsCount}
            handleChange={(event) => setRowsCount(+event.target.value)}
            controlHandler={setRowsCount}
         />
         <InputCounts
            className={inputClassName}
            label="Set the columns count"
            value={columnsCount}
            handleChange={(event) => setColumnsCount(+event.target.value)}
            controlHandler={setColumnsCount}
         />
         <InputCounts
            className={inputClassName}
            label="Set the count of cells to show the nearest value"
            value={cellsCount}
            handleChange={(event) => setCellsCount(+event.target.value)}
            controlHandler={setCellsCount}
         />
         <Button
            buttonText="Apply settings "
            buttonClickHandler={applySettingsHandler}
         />
         {isShowErrorHint ? <ErrorHint errorText={errorText} /> : null}
      </div>
   );
};

export default SettingsTable;
