import React from "react";
import "./InputCounts.scss";
import Button from "../../../Button";

const InputCounts = ({ id, value, handleChange,controlClickHandler }) => {
   return (
      <div className="input-settings">
         <Button
            className="input-control__button"
            buttonText="-"
            buttonClickHandler={() => controlClickHandler(++value)}
         />
         <input
            className="input-settings__input"
            type="number"
            id={id}
            onChange={handleChange}
            min="0"
            value={value}
         />
         <Button
            className="input-control__button"
            buttonText="+"
            buttonClickHandler={() => controlClickHandler(--value)}
         />
      </div>
   );
};

export default InputCounts;
