import React from "react";
import { useDispatch } from "react-redux";
import { decrementInput, incrementInput } from "../../../../redux/actions";

const InputControl = ({ id, type }) => {
   const dispatch = useDispatch();
   const controlClickHandler = (type) => {
      if (type === "+") {
         dispatch(incrementInput(id));
      } else if (type === "-") {
         dispatch(decrementInput(id));
      }
   };

   return (
      <div className="input-control" onClick={() => controlClickHandler(type)}>
         {type}
      </div>
   );
};

export default InputControl;
