import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { removeRow } from "../../../../../redux/actions";
import CrossButton from "../../../../CrossButton";
import IncrementCell from "./CellRenderer/IncrementCell";
import StaticCell from "./CellRenderer/StaticCell";
import SumCell from "./CellRenderer/SumCell";

const TableRow = ({ rowNumber, data }) => {
   const dispatch = useDispatch();
   const [showPercentageValue, setShowPercentageValue] = useState(false);
   const sumRow = data
      .map((data) => data.value)
      .reduce((sum, value) => sum + value, 0);
   const Renderer = CellRenderer(IncrementCell);
   const CellRenderer = data.map((cellData) => (
      <Renderer
         key={cellData.id}
         id={cellData.id}
         value={cellData.value}
         sumRow={sumRow}
      />
   ));

   return (
      <tr>
         <StaticCell key={rowNumber} value={rowNumber} />
         {rowCells}
         <SumCell
            key={sumRow}
            value={sumRow}
            sumMouseEnterHandler={() => setShowPercentageValue(true)}
            sumMouseLeaveHandler={() => setShowPercentageValue(false)}
         />
         <td>
            <CrossButton
               buttonClickHandler={() => dispatch(removeRow(rowNumber))}
            />
         </td>
      </tr>
   );
};

export default TableRow;
