import { INCREMENT_CELL, CREATE_MATRIX, REMOVE_ROW, ADD_ROW } from "./types";

export const createMatrix = (settings) => {
   return {
      type: CREATE_MATRIX,
      settings,
   };
};

export const incrementCell = (id, value) => {
   return {
      type: INCREMENT_CELL,
      id,
      value,
   };
};

export const removeRow = (rowCount) => {
   return {
      type: REMOVE_ROW,
      index: rowCount - 1,
   };
};

export const addRow = (columnsCount) => {
   return {
      type: ADD_ROW,
      columnsCount,
   };
};
