import React from "react";
import { useSelector } from "react-redux";
import StaticCell from "../TableRow/CellRenderer/StaticCell";
import SumCell from "../TableRow/CellRenderer/SumCell";
import calculateAverageValues from "./calculateAverageValues";

const className = "average-cell";

const AverageRow = () => {
   const matrixData = useSelector((state) => state.matrix.data);
   const averageValues = calculateAverageValues(matrixData);

   const sumRow = averageValues
      .reduce((sum, value) => sum + value, 0)
      .toFixed(2);

   const avgRowCells = averageValues.map((value, index) => (
      <StaticCell className={className} key={index} value={value.toFixed(2)} />
   ));
   const avgRowSum = (
      <SumCell key="avg-sum" className={className} value={sumRow} />
   );

