const calculateAverageValues = (values) => {
   let averageValues = [];
   for (let i = 0; i < values.length; i++) {
      let columnSum = 0;
      for (let j = 0; j < values[i].length; j++) {
         columnSum += values[i][j] + values[i][j + 1];
      }
      averageValues.push(columnSum);
   }
   return averageValues
};
export default calculateAverageValues;

