import React, { useMemo } from "react";
import { useSelector } from "react-redux";
import AverageRow from "./AverageRow";
import TableRow from "./TableRow";

const TableBody = () => {
   const matrix = useSelector((state) => state.matrix.data);
   let matrixRows = useMemo(
      () =>
         matrix.map((row, index) => (
            <TableRow data={row} rowCount={index + 1} key={index} />
         )),
      [matrix]
   );

   return (
      <tbody style={{ borderCollapse: "collapse", border: "1px solid black" }}>
         {matrixRows}
         <AverageRow />
      </tbody>
   );
};

export default TableBody;
