import React, { useMemo } from "react";
import { useSelector } from "react-redux";
import { cellRenderers } from "../../../../../CellRenderers";
import TableCell from "../TableRow/TableCell";

const AverageRow = () => {
   const matrix = useSelector((state) => state.matrix);
   const data = matrix.map((item) =>
      item.filter((data) => data.value).map((row) => row.value)
   );
   //current row values and next row values return the sum of values by equal index
   const averageValues = useMemo(
      () =>
         data
            .reduce((currentRow, nextRow) =>
               currentRow.map((value, index) => value + nextRow[index])
            )
            .map((value) => value / data.length),
      [data]
   );
   const getAvgRowLabel = (
      <TableCell key="avg" value="Avg" Renderer={cellRenderers.static} />
   );
   const getAvgRowCells = averageValues.map((value, index) => (
      <TableCell
         key={index}
         value={value.toFixed(2)}
         Renderer={cellRenderers.static}
      />
   ));
   const cells = [getAvgRowLabel, ...getAvgRowCells];

   return <tr>{cells}</tr>;
};

export default AverageRow;
