import React, { useEffect, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setMatrixRows } from "../../redux/actions";
import InputCounts from "./InputCounts";
import "./StartPage.scss";

const createMatrixRows = (matrixSettings) => {
   let matrixData = [];
   for (let i = 0; i < matrixSettings.rows; i++) {
      matrixData.push([]);
   }
   return matrixData;
};

const StartPage = () => {
   const matrixSettings = useSelector((state) => state.matrix.settings);
   const dispatch = useDispatch();

   const createMatrix = (matrixSettings) => {
      createMatrixRows(matrixSettings);
   };

   /*  const createMatrix = (rowsCount, columnsCount) => {
      let matrix = [];
      if (rowsCount === 0 || columnsCount === 0) {
         console.log("counts can't be 0");
      } else {
         const createRows = () => {
            for (let i = 0; i < rowsCount; i++) {
               matrix.push([]);
            }
         };
         const createColumns = () => {
            matrix.forEach((item, index) => {
               for (let i = 0; i < columnsCount; i++) {
                  const cellObject = {
                     id: String(index + 1).concat(i + 1),
                     value: Math.round(100 + Math.random() * 899),
                  };
                  item.push(cellObject);
               }
            });
         };
         createRows();
         createColumns();
      }
      props.createMatrix(matrix);
   }; */

   return (
      <div className="start-page">
         <h1>Welcome to Matrix Builder</h1>
         <InputCounts
            id="rows"
            label="Set the rows count"
            handleChange={(event) =>
               dispatch(setMatrixRows(event.target.value))
            }
         />
         <InputCounts
            id="columns"
            label="Set the columns count"
            // handleChange={handleChangeColumns}
         />
         <InputCounts
            id="cells"
            label="Set the count of cells to show the nearest value"
            // handleChange={handleChangeCells}
         />
         <button onClick={() => createMatrixRows()}>
            Apply settings of matrix
         </button>
      </div>
   );
};

export default StartPage;
