import React, { useMemo } from "react";
import { useDispatch } from "react-redux";
import { removeRow } from "../../../../redux/actions";
import CrossButton from "../../../CrossButton";
import IncrementCell from "./TableCell/IncrementCell";
import StaticCell from "./TableCell/StaticCell";

const TableRow = ({ rowCount, data }) => {
   const rowLabel = <StaticCell key={rowCount} value={rowCount} />;
   const rowCells = data.map((cellData) => (
      <IncrementCell
         key={cellData.id}
         id={cellData.id}
         value={cellData.value}
      />
   ));
   const sumRowCell = useMemo(() => {
      const sumRow = data
         .map((data) => data.value)
         .reduce((sum = 0, value) => sum + value);
      return <StaticCell key={sumRow} value={sumRow} />;
   }, [rowCells]);
   const cells = [rowLabel, ...rowCells, sumRowCell];
   const dispatch = useDispatch();

   return (
      <tr>
         {cells}
         <td>
            <CrossButton
               buttonClickHandler={() => dispatch(removeRow(rowCount))}
            />
         </td>
      </tr>
   );
};

export default TableRow;
