import React from "react";
import RowCell from "./RowCell";

const TableRow = (props) => {
   let rowCells = props.data.map((data, index) => (
      <RowCell key={data.id} value={data.value} />
   ));
   const rowValues = props.data.slice(1).map((data) => data.value);
   const sumRow = rowValues.reduce((sum, value) => sum + value);
   rowCells.push(<RowCell key={sumRow} value={sumRow} />);
   return <tr>{rowCells}</tr>;
};

export default TableRow;
