import React, { useMemo } from "react";
import TableCell from "./TableCell/TableCell";
import StaticCell from "./TableCell/StaticCell";

const TableRow = ({ data }) => {
   let matrixCells = () => {
      return data.map((cellData, index) => (
         <TableCell key={index} id={cellData.id} value={cellData.value} />
      ));
   };
   // if (cellData.rowNumber) {
   //    return <StaticCell key={index} value={cellData.rowNumber} />;
   // }
   const sumCell = useMemo(() => {
      return data
         .map((data) => data.value)
         .reduce((sum = 0, value) => sum + value);
   }, [matrixCells]);

   return (
      <tr>
         {matrixCells}
         <StaticCell value={sumCell} />
      </tr>
   );
};

export default TableRow;
