import "./InputCounts.scss";

const InputCounts = ({ id, label, value, handleChange, blurHandle }) => {
   // const [isFocus, setIsFocus] = useState(false);
   // console.log(isFocus);
   return (
      <div className="input-wrapper">
         <label htmlFor={id}>{label}</label>
         <input
            type="number"
            id={id}
            onChange={handleChange}
            min="0"
            value={value}
            // onFocus={() => setIsFocus(true)}
          /*   onBlur={() => {
               if (isFocus) {
                  console.log("blur");
               }
            }} */
         />
      </div>
   );
};

export default InputCounts;
