import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";

const AverageRow = () => {
   const data = useSelector((state) =>
      state.matrix.data.map((item) =>
         item.filter((data) => data.value).map((row) => row.value)
      )
   );
   const columnsCount = useSelector((state) => state.matrix.settings.columns);
   const [averageValues, setAverageValues] = useState([]);

   useEffect(() => {
      const averageSum = [];
      for (let rowIndex = 0; rowIndex < data.length; rowIndex++) {
         let sumColumn = 0;
         for (
            let columnIndex = 0;
            columnIndex < data[rowIndex].length;
            columnIndex++
         ) {
            sumColumn = sumColumn + data[rowIndex][columnIndex];
         }
         console.log(sumColumn);
      }
   }, [data]);

   return <tr></tr>;
};

export default AverageRow;
