import {
   INCREMENT_CELL,
   CREATE_MATRIX,
   SET_MATRIX_COLUMNS,
   SET_MATRIX_ROWS,
   REMOVE_ROW,
   ADD_ROW,
} from "../types";

const initialState = { settings: { rows: 0, columns: 0, cells: 0 }, data: [] };

const matrixReducer = (state = initialState, action) => {
   let settings, data;
   switch (action.type) {
      case SET_MATRIX_ROWS:
         settings = { ...state.settings };
         settings.rows = action.payload;
         return { ...state, settings };
      case SET_MATRIX_COLUMNS:
         settings = { ...state.settings };
         settings.columns = action.payload;
         return { ...state, settings };

      case CREATE_MATRIX:
         data = [...state.data];
         settings = { ...state.settings };
         for (let i = 0; i < settings.rows; i++) {
            data.push([]);
         }
         data.forEach((item, index) => {
            for (let i = 0; i < settings.columns; i++) {
               const cellObject = {
                  id: String(index + 1).concat(i + 1),
                  value: Math.round(100 + Math.random() * 899),
               };
               item.push(cellObject);
            }
         });
         return { ...state, data };
      case INCREMENT_CELL:
         data = [...state.data];
         data
            .find((row) => row.find((item) => item.id === action.id))
            .filter((item) => item.id === action.id)[0].value++;
         return { ...state, data };
      case REMOVE_ROW:
         data = [...state.data];
         data.splice(action.index, 1);
         return { ...state, data };
      case ADD_ROW:
         data = [...state.data];
         settings = { ...state.settings };
         let newRow = [];
         for (let i = 0; i < settings.columns; i++) {
            const cellObject = {
               id: data.length.concat(i + 1),
               value: Math.round(100 + Math.random() * 899),
            };
            newRow.push(cellObject);
         }
         data.push(newRow);
         console.log(data);
      default:
         return state;
   }
};

export default matrixReducer;
