import React, { useMemo } from "react";
import { useSelector } from "react-redux";
import NumerableCell from "../TableRow/TableCell/NumerableCell";

const AverageRow = () => {
   const columns = useSelector((state) => state.matrix.settings.columns);
   const data = useSelector((state) =>
      state.matrix.data.map((item) =>
         item.filter((data) => data.value).map((row) => row.value)
      )
   );
   const averageValues = useMemo(() => {
      //current row values and next row values return the sum of values by equal index
      return data
         .reduce((current, next) =>
            current.map((value, index) => value + next[index])
         )
         .map((value) => value / data.length);
   }, [data]);
   const averageCells = [<NumerableCell key="avg" value="Avg" />].concat(
      averageValues.map((value, index) => (
         <NumerableCell key={index} value={Math.round(value)} />
      ))
   );

   return <tr>{averageCells}</tr>;
};

export default AverageRow;
