import React from "react";
import { useDispatch } from "react-redux";
import {
   createMatrix,
   setMatrixColumns,
   setMatrixRows,
} from "../../redux/actions";
import Button from "../Button";
import InputCounts from "./InputCounts";
import "./StartPage.scss";

const StartPage = ({ tableSettings, setTableSettings }) => {
   const dispatch = useDispatch();
   const setTableRows = (value) => {
      const settings = { ...tableSettings, rows: value };
      setTableSettings(settings);
   };
   console.log(tableSettings)

   return (
      <div className="start-page">
         <h1>Welcome to Matrix Builder</h1>
         <InputCounts
            id="rows"
            label="Set the rows count"
            handleChange={(event) => setTableRows(event.target.value)}
            value={tableSettings.rows}
         />
         <InputCounts
            id="columns"
            label="Set the columns count"
            handleChange={(event) =>
               dispatch(setMatrixColumns(event.target.value))
            }
            value={tableSettings.columns}
         />
         <InputCounts
            id="cells"
            label="Set the count of cells to show the nearest value"
            // handleChange={handleChangeCells}
            value={tableSettings.cells}
         />
         <Button
            buttonText="Apply settings of matrix"
            buttonClickHandler={() => dispatch(createMatrix())}
         />
      </div>
   );
};

export default StartPage;
